package com.javaex.practice;//Yet Done//예상:3*4꼴의 별=>틀렸다. 왜 일렬로 쭉이지?

public class Ex03 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x, y;
		for(x=0; x<4; x++) {
			for(y=2; y>=0; y--) {
				System.out.print("*");
			}
		} System.out.println("");
		
	}

}
